import React, { useState, useEffect } from "react";




function App() {

  const drumSounds = [

    { id: 'Heater1', letter: 'q', keycode: "81", src: "https://s3.amazonaws.com/freecodecamp/drums/Heater-1.mp3" },
    { id: 'Heater2', letter: 'W', keycode: "87", src: "https://s3.amazonaws.com/freecodecamp/drums/Heater-2.mp3" },
    { id: 'Heater3', letter: 'E', keycode: "69", src: "https://s3.amazonaws.com/freecodecamp/drums/Heater-3.mp3" },
    { id: 'Heater4', letter: 'A', keycode: "65", src: "https://s3.amazonaws.com/freecodecamp/drums/Heater-4_1.mp3" },
    { id: 'Clap', letter: 'S', keycode: "83", src: "https://s3.amazonaws.com/freecodecamp/drums/Heater-6.mp3" },
    { id: 'HH', letter: 'D', keycode: "68", src: "https://s3.amazonaws.com/freecodecamp/drums/Dsc_Oh.mp3" },
    { id: 'Kick Hat', letter: 'Z', keycode: "90", src: "https://s3.amazonaws.com/freecodecamp/drums/Kick_n_Hat.mp3" },
    { id: "Kicka", letter: 'X', keycode: "88", src: "https://s3.amazonaws.com/freecodecamp/drums/RP4_KICK_1.mp3" },
    { id: 'Closed', letter: 'C', keycode: "67", src: "https://s3.amazonaws.com/freecodecamp/drums/Cev_H2.mp3" },

  ]

  const [clip, setClip] = useState("");

  const [power, setPower] = useState(false)

  useEffect(() => {
    document.addEventListener("keydown", handleKeyDown)

  });


  /* useEffect(() => {
    const timer = setTimeout(() => {
      document.getElementById("Heater1").classList.add("pressed")
    }, 1000);
    return () => clearTimeout(timer)

  }, []);

  useEffect(() => {
    const timer = setTimeout(() => {
      document.getElementById("Heater1").classList.remove("pressed")
    }, 2000);
    return () => clearTimeout(timer)

  }, []); */




  const handleClick = (event) => {
    if (power) {
      let clickedButton = event.target.id;
      /* const timer = setTimeout(() => {
        document.getElementById(clickedButton).classList.add("pressed")
      }, 0); */

      drumSounds.forEach(elem => {
        if (elem.id === clickedButton) {
          let audio = new Audio(elem.src);
          audio.play();
          document.getElementById(elem.id).classList.add("pressed");
          setTimeout(() => {
            document.getElementById(elem.id).classList.remove("pressed")
          }, 1000)
        }
        return () => clearTimeout();
      })
      setClip(clickedButton);
    }

    /*  const timer2 = setInterval(() => {
       document.getElementById(clickedButton).classList.remove("pressed")
     }, 1000); */
  }

  const handleKeyDown = (event) => {
    if (power) {
      let clickedButton;
      let pressedKey = event.keyCode;

      drumSounds.forEach(elem => {
        if (elem.keycode == pressedKey) {
          let audio = new Audio(elem.src);
          audio.play();
          clickedButton = elem.id;
          document.getElementById(elem.id).classList.add("pressed");
          setTimeout(() => {
            document.getElementById(elem.id).classList.remove("pressed")
          }, 1000)

        }
        return () => clearTimeout();
      });
      setClip(clickedButton);
    }
  }

  const handlePowerOn = () => {
    setPower(true);
    console.log(power);
  }

  const handlePowerOff = () => {
    setPower(false);
    console.log(power);
  }

  return (
    <div>
      <h1>Drum Machine</h1>
      <div class="wrapper">
        <div class="display"> {clip} </div>
        <div class="container">
          <div class="drum-master row">
            <div onClick={handleClick}
              className="drum-pad col-sm-3"
              id="Heater1"
              value="id">
              Q
    </div>
            <div onClick={handleClick} class="drum-pad col-sm-3" id="Heater2" letter={drumSounds[0].letter}>
              W
    </div>
            <div onClick={handleClick} class="drum-pad col-sm-3" id="Heater3">
              E
    </div>
          </div>

          <div class="drum-master row">
            <div onClick={handleClick} class="drum-pad col-sm-3" id="Heater4">
              A
    </div>
            <div onClick={handleClick} class="drum-pad col-sm-3" id="Clap">
              S
    </div>
            <div onClick={handleClick} class="drum-pad col-sm-3" id="HH">
              D
    </div>
          </div>

          <div class="drum-master row">
            <div onClick={handleClick} id="Kick Hat" class="drum-pad col-sm-3">
              Z
    </div>
            <div onClick={handleClick} class="drum-pad col-sm-3" id="Kicka">
              X
    </div>
            <div onClick={handleClick} class="drum-pad col-sm-3" id="Closed">
              C
    </div>
          </div>

          <div className="power"
            style={{ backgroundColor: power ? "chartreuse" : "red" }}>
            <div className="power-header">

            </div>
            <button onClick={handlePowerOn} className="power-on"> On </button>
            <button onClick={handlePowerOff} className="power-off"> Off </button>
          </div>

        </div>
      </div>
    </div>
  );

}

export default App;
